CREATE TABLE IF NOT EXISTS qcml (QC_ID_PK INTEGER PRIMARY KEY ASC AUTOINCREMENT, qcml_file VARCHAR(45) NULL);
CREATE TABLE IF NOT EXISTS quality_assessment (QA_ID_PK INTEGER PRIMARY KEY ASC AUTOINCREMENT, id VARCHAR(45) UNIQUE NOT NULL, is_set INTEGER(1) NOT NULL DEFAULT 0, QC_ID_FK INTEGER NOT NULL, FOREIGN KEY (QC_ID_FK) REFERENCES qcml (QC_ID_PK));
CREATE TABLE IF NOT EXISTS cv (CV_ID_PK INTEGER PRIMARY KEY ASC AUTOINCREMENT, id VARCHAR(45) UNIQUE NOT NULL, full_name VARCHAR(45) NULL, version VARCHAR(45) NULL, uri VARCHAR(45) NULL);
CREATE TABLE IF NOT EXISTS cv_list (QC_ID_FK INTEGER NOT NULL, CV_ID_FK INTEGER NOT NULL, PRIMARY KEY (QC_ID_FK, CV_ID_FK), FOREIGN KEY (QC_ID_FK ) REFERENCES qcml (QC_ID_PK ) FOREIGN KEY (CV_ID_FK ) REFERENCES cv (CV_ID_PK ));
CREATE TABLE IF NOT EXISTS quality_parameter (QP_ID_PK INTEGER PRIMARY KEY ASC AUTOINCREMENT, id VARCHAR(45) UNIQUE NOT NULL, name VARCHAR(45) NOT NULL, value VARCHAR(45) NULL, accession VARCHAR(45) NULL, unit_name VARCHAR(45) NULL, unit_accession VARCHAR(45) NULL, flag BOOLEAN, cv_ref INTEGER NOT NULL, unit_cv_ref INTEGER NULL, QA_ID_FK INTEGER NOT NULL, FOREIGN KEY (unit_cv_ref ) REFERENCES cv (CV_ID_PK ), FOREIGN KEY (cv_ref ) REFERENCES cv (CV_ID_PK ), FOREIGN KEY (QA_ID_FK) REFERENCES quality_assessment (QA_ID_PK));
CREATE TABLE IF NOT EXISTS threshold (TH_ID_PK INTEGER PRIMARY KEY ASC AUTOINCREMENT, name VARCHAR(45) NOT NULL, value VARCHAR(45) NULL, accession VARCHAR(45) NULL, unit_name VARCHAR(45) NULL, unit_accession VARCHAR(45) NULL, cv_ref INTEGER NOT NULL, unit_cv_ref INTEGER NULL, threshold_filename VARCHAR(45), QP_ID_FK INTEGER NOT NULL, FOREIGN KEY (unit_cv_ref ) REFERENCES cv (CV_ID_PK ), FOREIGN KEY (cv_ref ) REFERENCES cv (CV_ID_PK ), FOREIGN KEY (QP_ID_FK) REFERENCES quality_parameter (QP_ID_PK));
CREATE TABLE IF NOT EXISTS attachment_parameter (AP_ID_PK INTEGER PRIMARY KEY ASC AUTOINCREMENT, id VARCHAR(45) UNIQUE NOT NULL, name VARCHAR(45) NOT NULL, value VARCHAR(45) NULL, unit_name VARCHAR(45) NULL, unit_accession VARCHAR(45) NULL, accession VARCHAR(45) NULL, binary BLOBs, unit_cv_ref INTEGER NULL, cv_ref INTEGER NOT NULL, quality_parameter_ref VARCHAR(45) NULL, QA_ID_FK INTEGER NOT NULL, FOREIGN KEY (unit_cv_ref ) REFERENCES cv (CV_ID_PK ), FOREIGN KEY (cv_ref ) REFERENCES cv (CV_ID_PK ), FOREIGN KEY (QA_ID_FK ) REFERENCES quality_assessment (QA_ID_PK ));
CREATE TABLE IF NOT EXISTS table_attachment (TA_ID_PK INTEGER PRIMARY KEY ASC AUTOINCREMENT, AP_ID_FK INTEGER NOT NULL, FOREIGN KEY (AP_ID_FK) REFERENCES attachment_parameter (AP_ID_PK));
CREATE TABLE IF NOT EXISTS table_column (TC_ID_PK INTEGER PRIMARY KEY ASC AUTOINCREMENT, name VARCHAR(100), TA_ID_FK INTEGER NOT NULL, FOREIGN KEY (TA_ID_FK) REFERENCES table_attachment (TA_ID_PK));
CREATE TABLE IF NOT EXISTS table_row (TR_ID_PK INTEGER PRIMARY KEY ASC AUTOINCREMENT, row_num INTEGER, TA_ID_FK INTEGER NOT NULL, FOREIGN KEY (TA_ID_FK) REFERENCES table_attachment (TA_ID_PK));
CREATE TABLE IF NOT EXISTS table_value (TV_ID_PK INTEGER PRIMARY KEY ASC AUTOINCREMENT, value VARCHAR(100), type VARCHAR(100), TR_ID_FK INTEGER NOT NULL, TC_ID_FK INTEGER NOT NULL, FOREIGN KEY (TC_ID_FK) REFERENCES table_column (TC_ID_PK) );
